mapper.sql:
		select 
            node.id             as id
        from
            alf_node_props_denorm node
        where 
            node.type_qname_id <> #{sysDeletedType}
            AND node.store_id = #{storeId}


                    AND node.transaction_id > #{sinceTxId}


            AND



                            AND


                             node.${item.denormalizedFieldName} >= #{item.value}


                             node.${item.denormalizedFieldName} = #{item.value}


                           	node.${item.denormalizedFieldName} IS NOT NULL


                           	node.${item.denormalizedFieldName} IS NOT NULL


                           	node.${item.denormalizedFieldName} > #{item.value}


                          	node.${item.denormalizedFieldName} < #{item.value}


                          	node.${item.denormalizedFieldName} <= #{item.value}


                            ${item.fieldAndFunction} IN #{val}


                          	${item.fieldAndFunction} NOT IN #{val}


                          	node.${item.denormalizedFieldName} LIKE #{item.value}  


                          	node.${item.denormalizedFieldName} NOT LIKE #{item.value}  


                          	node.${item.denormalizedFieldName} <> #{item.value}


                             1 = 1





		            order by




		                         node.${item.denormalizedFieldName} ${item.value}
